package logIn;import org.junit.Assert;import org.junit.Test;import parentTest.ParentTest;public class LogInNew extends ParentTest {    @Test    public void validLogin(){        loginPage.openLoginPage();        loginPage.enterLogin("Student");        loginPage.enterPassword("909090");        loginPage.clickButtonVhod();        Assert.assertTrue("Avatar is not present", homePage.isAvatarDisplayed());    }    @Test    public void inValidLogin(){        loginPage.openLoginPage();        loginPage.enterLogin("Student");        loginPage.enterPassword("9090");        loginPage.clickButtonVhod();        Assert.assertFalse("Avatar is present", homePage.isAvatarDisplayed());    }}